mapper.select:

select *
from(
select rownum as rnum, A.*
from (select
 qno, member_id,ques_subject,ques_question,ques_date,status
from member_question
 WHERE member_id LIKE '%'||#{keyword}||'%'
) A
where rownum <=#{displayPost}+#{postNum})
where rnum > #{displayPost}

mapper.update:
update member_question
set
ques_subject = #{ques_subject},
member_id = #{member_id},
ques_question =#{ques_question}
where qno = #{qno}
