mapper.select:


 	 select *
from(
select rownum as rnum, A.*
from (select
mno, member_id,member_name, member_pw,member_born_date,member_gender,member_email,member_comment,member_join_date	,authority,enabled
from member
WHERE member_name LIKE '%'||#{keyword}||'%'
) A
where rownum <=#{displayPost}+#{postNum})
where rnum > #{displayPost}
